n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "DOCAI_DOCUMENT_VIEW_ACTIVITY_BI" 
BEFORE INSERT
ON docai_document_view_activity
FOR EACH ROW
BEGIN
  :new.crt_ts := SYSTIMESTAMP;
  :new.crt_by := NVL(v('APP_USER'), USER);
  :new.view_ts := NVL(:new.view_ts, SYSTIMESTAMP);
END;
